import { Text, View, Image, StyleSheet, TouchableOpacity } from 'react-native'
import { GooglePlacesAutocomplete } from 'react-native-google-places-autocomplete'
import { SafeAreaView } from 'react-native-safe-area-context'
import { GOOGLE_APIKEY } from '@env'
import React, { useState, useEffect } from 'react'
import Slider from '@react-native-community/slider'
import { useFirstRender } from './helpers'

import { Element } from 'react'
class SliderExample extends React.Component {
  static defaultProps = {
    value: 0,
  }

  state = {
    value: this.props.value,
  }

  render() {
    return (
      <View>
        <Text style={styles.text}>
          {this.state.value && +this.state.value.toFixed(3)}
        </Text>
        <Slider
          step={0.5}
          style={styles.slider}
          {...this.props}
          onValueChange={(value) => this.setState({ value: value })}
        />
      </View>
    )
  }
}

const styles = StyleSheet.create({
  slider: {
    width: 300,
    opacity: 1,
    height: 50,
    marginTop: 10,
  },
  text: {
    fontSize: 14,
    textAlign: 'center',
    fontWeight: '500',
    margin: 0,
  },
})

const Main = () => {
  const [tempInside, setTempInside] = useState(21.0)
  const [scale, setScale] = useState(null)
  const firstRender = useFirstRender()

  useEffect(() => {
    if (firstRender) return
    if (scale) {
      setTempInside(
        (tempInside) => Math.round((cToF(tempInside) + 0.1) * 10) / 10,
      )
    } else if (!scale) {
      setTempInside(
        (tempInside) => Math.round((fToC(tempInside) + 0.1) * 10) / 10,
      )
    }
  }, [scale])

  return (
    <SafeAreaView style={{ backgroundColor: 'white', flex: 1 }}>
      {/* image */}
      <Image
        resizeMode="contain"
        style={{
          height: 50,
          alignSelf: 'center',
          marginTop: 20,
        }}
        source={require('../assets/logo_black.png')}
      />

      {/* location input */}
      <View>
        <Text style={{ textAlign: 'center', marginTop: 50, fontSize: 18 }}>
          1. location
        </Text>
        <GooglePlacesAutocomplete
          placeholder="type your city"
          nearbyPlacesAPI="GooglePlacesSearch"
          debounce={400}
          styles={toInputBoxStyles}
          enablePoweredByContainer={false}
          query={{
            key: GOOGLE_APIKEY,
            language: 'en',
          }}
          fetchDetails={true}
          onPress={(data, details = null) => {}}
          returnKeyType={'search'}
        />
      </View>

      {/* slider */}
      <View style={[{ flex: 1, alignItems: 'center', marginTop: 100 }]}>
        <View style={{ top: -60 }}>
          <Text style={{ fontSize: 18 }}>2. room temperature</Text>
          <TouchableOpacity>
            <Text>Hi</Text>
          </TouchableOpacity>
        </View>
        <Text style={{ fontSize: 50, fontWeight: '700', left: -40, top: 110 }}>
          {tempInside.toFixed(1)}
          <Text style={{ fontWeight: '300' }}> Â°{!scale ? 'C' : scale}</Text>
        </Text>
        <Slider
          minimumValue={10}
          maximumValue={38}
          step={0.1}
          style={{
            width: 300,
            top: 70,
            right: -110,
            transform: [{ rotate: '270deg' }],
          }}
          minimumTrackTintColor="#94000078"
          maximumTrackTintColor="#0064c16d"
          onValueChange={(value) => setTempInside(value)}
          value={tempInside}
        />
      </View>

      {/* button */}
    </SafeAreaView>
  )
}

const toInputBoxStyles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    marginTop: 10,
    flex: 0,
  },
  textInput: {
    backgroundColor: '#dddddf86',
    borderRadius: 0,
    fontSize: 18,
    paddingLeft: 20,
    paddingRight: 30,
  },
  textInputContainer: {
    marginHorizontal: 40,
  },
  row: {
    marginHorizontal: 40,
  },
  separator: {
    marginHorizontal: 40,
  },
})

export default Main
